	[Activity (Label = "ListadoDenunciasActivity", Theme="@style/MyTheme")]			
	public class ListadoDenunciasActivity : Android.Support.V7.App.ActionBarActivity
	{
		private CardView card1;
		private LinearLayout layout;
		private TextView text;
		protected override void OnCreate (Bundle bundle)
		{
			base.OnCreate (bundle);

			// Create your application here
			SetContentView(Resource.Layout.ListadoDenuncias);
			layout = FindViewById<LinearLayout> (Resource.Id.layout);
			Shared.denuncias.OrderBy (d => DateTime.ParseExact(d.creado_en, "yyyy-MM-dd HH:mm:ss",System.Globalization.CultureInfo.InvariantCulture));
			foreach (var denuncia in Shared.denuncias) {
				View cardview = LayoutInflater.From (this).Inflate(Resource.Layout.CardView, layout, false);
				cardview.FindViewById<TextView> (Resource.Id.Tipo).Text = denuncia.nombretipo;
				cardview.FindViewById<TextView> (Resource.Id.Ciudad).Text = denuncia.ciudad;
				cardview.FindViewById<TextView> (Resource.Id.Usuario).Text = denuncia.nombre;
				layout.AddView (cardview);
				byte[] image;
				if (denuncia.imagebase64.Length <= 0) {
					WebClient clienteUpload = new WebClient ();
					NameValueCollection parametros = new NameValueCollection ();
					parametros.Add ("iddenuncia", denuncia.iddenuncia.ToString ());
					clienteUpload.UploadValuesAsync (MainActivity.WebServices ["getDenunciaPicture"], parametros);
					clienteUpload.UploadValuesCompleted += (object sender, UploadValuesCompletedEventArgs e) => {
						denuncia.imagebase64 = Encoding.UTF8.GetString (e.Result);	
						Console.WriteLine (denuncia.imagebase64);
						if (denuncia.imagebase64 != null && denuncia.imagebase64 != "") {
							image = Convert.FromBase64String (denuncia.imagebase64);
							cardview.FindViewById<ImageView> (Resource.Id.Picture).SetImageBitmap (BitmapFactory.DecodeByteArray (image, 0, image.Length));
						} 
					};
				} else {
					image = Convert.FromBase64String (denuncia.imagebase64);
					cardview.FindViewById<ImageView> (Resource.Id.Picture).SetImageBitmap (BitmapFactory.DecodeByteArray (image, 0, image.Length));
				}
				cardview.Click += (object sender, EventArgs e) => {
					Shared.denuncia = denuncia;
					StartActivity (typeof(DetalleDenunciaActivity));	
				};
			}
//			card1 = FindViewById<CardView>(Resource.Id.card1);
